            return <div> 
                {
                    champion.Skins
                }
            </div>
        }  else{
        }
        return<div>LOADING</div>
       }
    return (
        <div className={styles.Main}>
            <div className={styles.ChampInfoCont}>
                <div className={styles.ImageContainer}>
                    <img src={urlSplash} />
                </div>
                <div className={styles.Info}>
                    infromacion
                </div>
            </div>
            <div className={styles.Skins}>
                <Skins/>
            </div>
        </div>

    )
}

export default ChampScreen
